INSERT INTO `tbl_email` (`id`, `to_email`, `from_email`, `re_email`, `template`, `status`) VALUES
(1, 'info@twinne.com', NULL, 'info@twinne.com', 'Dear Administration,\r\n\r\nA new order has been placed at Twinne!\r\n\r\nOrder ID is {order_sku}\r\n\r\nOrder details:\r\n\r\n{order_table}\r\n\r\nWe are working right now to ship your order the soonest possible. You will receive a confirmation by email once the order is delivered to you. \r\n\r\nIn case you need help, feel free to contact our customer service through the {support_system} or the {contact_page}.\r\n\r\nHoping to see you soon\r\n\r\nBest,\r\n\r\nThe Twinne Team\r\nhttp://twinne.com\r\n', 'order admin'),
(2, NULL, 'admin@twinne.com', 'admin@twinne.com', 'Dear {name},\r\n\r\nThank you for choosing to buy from Twinne!\r\n\r\nYour order ID is {order_sku}\r\n\r\nOrder details:\r\n\r\n{order_table}\r\n\r\nWe are working right now to ship your order the soonest possible. You will receive a confirmation by email once the order is delivered to you. \r\n\r\nIn case you need help, feel free to contact our customer service through the {support system} or the {contact page}.\r\n\r\nHoping to see you soon\r\n\r\nBest,\r\n\r\nThe Twinne Team\r\nhttp://twinne.com\r\n', 'user order placed'),
(3, NULL, 'admin@twinne.com', 'admin1@twinne.com', '<p>\n	Dear User,</p>\n<p>\n	Your order has been shipped. Order details are given below:</p>\n<p>\n	Order id : {order_id}</p>\n<p>\n	User id : {user_id}</p>\n<p>\n	Order date : {order_date}</p>\n<p>\n	&nbsp;</p>\n<p>\n	Thanks,</p>\n<p>\n	Team Twinne</p>\n', 'user order shipped'),
(4, NULL, 'admin@twinne.com', 'admin1@twinne.com', '<p>\n	Dear User,</p>\n<p>\n	Thanks for communicating with us.</p>\n<p>\n	We will communicate with you soon.</p>\n<p>\n	&nbsp;</p>\n<p>\n	Thanks,</p>\n<p>\n	Team Twinne</p>\n', 'user contacted'),
(5, NULL, 'admin@twinne.com', 'admin@twinne.com', 'Dear {name},\r\n\r\nThank you for joining Twinne, the only website where you can buy t-shirts created by top world-wide artists!\r\n\r\nYou can start now {browsing_the_gallery} and adding products to your {wish_list}\r\n\r\nIn case you need help, feel free to contact our customer service through the {support_system} or the {contact_page}.\r\n\r\nBest,\r\n\r\nThe Twinne Team\r\nhttp://twinne.com\r\n', 'user account'),
(6, NULL, 'super_admin@twinne.com', 'admin@twinne.com', 'Dear <user_id>\nCongratulations!! your design has been approved.Keep it up.', 'design approved');
